    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        if(jTextField1.getText().isEmpty() || jTextField2.getText().isEmpty() || jTextField3.getText().isEmpty() || jTextField4.getText().isEmpty() ){
           JOptionPane.showMessageDialog(null,"Porfavor Ingrese todas las casillas!");
        }else{
            String title = jTextField1.getText();
            String inicio = jTextField2.getText();
            String termino = jTextField3.getText();
            String lugar = jTextField4.getText();
            
            
            Exposicion ee = new Exposicion(title,inicio,termino,lugar);
            galery.agregarExposicion(ee);
            JOptionPane.showMessageDialog(null,"Exposicion agregada con exito");
            dispose();
        }
    }//GEN-LAST:event_jButton1ActionPerformed



